package hello;

public class Constant {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final int Max_num = 100;	// 상수 사용시 final 사용
		final int Min_num;
		Min_num = 0;	//사용전에 초기화하지 않으면 오류 발생(변수값입력하지않을시)
		
		System.out.println(Max_num);System.out.println(Min_num);
		
		// 한번 지정한 값은 상수값은 변경할 수 없다.
		
		
	}

}
